export const openWindow=function(windowName){return new Promise((resolve,reject)=>{overwolf.windows.obtainDeclaredWindow(windowName,result=>{if('success'!==result.status)reject();else{const windowId=result.window.id;overwolf.windows.restore(windowId,result=>{'success'===result.status?resolve(windowId):reject()})}})})};export const closeWindow=function(windowName){return new Promise((resolve,reject)=>{overwolf.windows.obtainDeclaredWindow(windowName,result=>{if('success'!==result.status)reject();else{const windowId=result.window.id;overwolf.windows.close(windowId,result=>{'success'===result.status?resolve(windowId):reject()})}})})};export const closeApp=function(){closeWindow('ControllerWindow')};export function dragMove(){overwolf.windows.getCurrentWindow(function(result){'success'==result.status&&overwolf.windows.dragMove(result.window.id)})}export function minimize(){overwolf.windows.getCurrentWindow(function(result){'success'==result.status&&overwolf.windows.minimize(result.window.id)})}// Used by html files
window.closeApp=closeApp,window.dragMove=dragMove,window.minimize=minimize;